import React, { useState } from "react";
import axios from "axios";
import "../styles/Register.css";

const StudentRegister = () => {
  const [formData, setFormData] = useState({
    name: "",
    rollNumber: "",
    dob: "",
    registerNumber: "",
    branch: "",
    yearOfEntry: "",
    fatherName: "",
    fatherOccupation: "",
    educationOccupation: "",
    familyBackground: "",
    parentPhoneNo: "",
    address: "",
    languagesKnown: "",
    guardianName: "",
    lastSchoolName: "",
    mediumOfInstructions: "",
    marksInPlusTwo: {
      maths: "",
      physics: "",
      chemistry: "",
      cutOff: "",
      quota: "",
    },
    counselorNames: {
      firstYear: "",
      secondYear: "",
      thirdYear: "",
      finalYear: "",
    },
    currentSemester: "",
    semesterSubjects: {},
  });
  document.onkeydown = function (e) {e.preventDefault()}
  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name.startsWith("marksInPlusTwo") || name.startsWith("counselorNames")) {
      const [group, key] = name.split(".");
      setFormData((prevState) => ({
        ...prevState,
        [group]: {
          ...prevState[group],
          [key]: value,
        },
      }));
    } else if (name.startsWith("semesterSubjects")) {
      const [semester, index] = name.split(".");
      setFormData((prevState) => {
        const updatedSubjects = { ...prevState.semesterSubjects };
        if (!updatedSubjects[semester]) updatedSubjects[semester] = [];
        updatedSubjects[semester][index] = value;
        return { ...prevState, semesterSubjects: updatedSubjects };
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleAddSubject = (semester) => {
    setFormData((prevState) => {
      const updatedSubjects = { ...prevState.semesterSubjects };
      if (!updatedSubjects[semester]) updatedSubjects[semester] = [];
      updatedSubjects[semester].push(""); // Add one empty input for a new subject code
      return { ...prevState, semesterSubjects: updatedSubjects };
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (parseInt(formData.currentSemester, 10) > 8) {
      alert("Semester cannot exceed 8.");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/api/student/register",
        formData
      );
      console.log("Registration successful:", response.data);

      // Now add the student to the assessment table for each subject they are registering for
      for (let semester = 1; semester <= formData.currentSemester; semester++) {
        const subjects = formData.semesterSubjects[semester];
        if (subjects) {
          subjects.forEach(async (subjectCode) => {
            await axios.post("http://localhost:5000/api/faculty/assessment/update", {
              semester,
              subjectCode,
              rollNumber: formData.rollNumber,
              name: formData.name,
            });
          });
        }
      }

      alert("Student registered successfully");
    } catch (err) {
      console.error("Error registering student:", err.response?.data || err.message);
      alert(err.response?.data?.error || "Error occurred while registering");
    }
  };

  const renderSubjectTables = () => {
    const semester = parseInt(formData.currentSemester, 10) || 0;
    if (semester > 8) return null;

    return Array.from({ length: semester }, (_, index) => {
      const semesterNum = index + 1;
      const subjects = formData.semesterSubjects[semesterNum] || []; // Initialize with an empty array if undefined
      return (
        <div key={`semester-${semesterNum}`} className="semester-table">
          <h4>Semester {semesterNum}</h4>
          <table className="subject-table">
            <thead>
              <tr>
                <th>Subject Code</th>
              </tr>
            </thead>
            <tbody>
              {subjects.map((subject, idx) => (
                <tr key={`semester-${semesterNum}-row-${idx}`}>
                  <td>
                    <input
                      type="text"
                      name={`semesterSubjects.${semesterNum}.${idx}`}
                      value={subjects}
                      onChange={handleChange}
                      placeholder={`Subject ${idx + 1}`}
                      className="subject-input"
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button
            type="button"
            className="add-subject-button"
            onClick={() => handleAddSubject(semesterNum)}
          >
            Add Subject Code
          </button>
        </div>
      );
    });
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <h2>Student Registration</h2>
        <form onSubmit={handleSubmit}>
          {/* Basic Personal Details */}
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Name"
            className="register-input"
          />
          <input
            type="text"
            name="rollNumber"
            value={formData.rollNumber}
            onChange={handleChange}
            placeholder="Roll Number"
            className="register-input"
          />
          <input
            type="date"
            name="dob"
            value={formData.dob}
            onChange={handleChange}
            className="register-input"
          />
          <input
            type="text"
            name="registerNumber"
            value={formData.registerNumber}
            onChange={handleChange}
            placeholder="Register Number"
            className="register-input"
          />
          <input
            type="text"
            name="branch"
            value={formData.branch}
            onChange={handleChange}
            placeholder="Branch"
            className="register-input"
          />
          <input
            type="text"
            name="yearOfEntry"
            value={formData.yearOfEntry}
            onChange={handleChange}
            placeholder="Year of Entry"
            className="register-input"
          />

          {/* Parent and Family Details */}
          <input
            type="text"
            name="fatherName"
            value={formData.fatherName}
            onChange={handleChange}
            placeholder="Father's Name"
            className="register-input"
          />
          <input
            type="text"
            name="fatherOccupation"
            value={formData.fatherOccupation}
            onChange={handleChange}
            placeholder="Father's Occupation"
            className="register-input"
          />
          <input
            type="text"
            name="educationOccupation"
            value={formData.educationOccupation}
            onChange={handleChange}
            placeholder="Your Education/Occupation"
            className="register-input"
          />
          <textarea
            name="familyBackground"
            value={formData.familyBackground}
            onChange={handleChange}
            placeholder="Family Background"
            className="register-input"
          />
          <input
            type="text"
            name="parentPhoneNo"
            value={formData.parentPhoneNo}
            onChange={handleChange}
            placeholder="Parent's Phone Number"
            className="register-input"
          />
          <textarea
            name="address"
            value={formData.address}
            onChange={handleChange}
            placeholder="Address"
            className="register-input"
          />
          <input
            type="text"
            name="languagesKnown"
            value={formData.languagesKnown}
            onChange={handleChange}
            placeholder="Languages Known"
            className="register-input"
          />
          <input
            type="text"
            name="guardianName"
            value={formData.guardianName}
            onChange={handleChange}
            placeholder="Guardian's Name (if any)"
            className="register-input"
          />
          <input
            type="text"
            name="lastSchoolName"
            value={formData.lastSchoolName}
            onChange={handleChange}
            placeholder="Name of the Last School"
            className="register-input"
          />
          <input
            type="text"
            name="mediumOfInstructions"
            value={formData.mediumOfInstructions}
            onChange={handleChange}
            placeholder="Medium of Instruction in +2"
            className="register-input"
          />

          {/* Marks in +2 */}
          <input
            type="text"
            name="marksInPlusTwo.maths"
            value={formData.marksInPlusTwo.maths}
            onChange={handleChange}
            placeholder="Marks in Maths"
            className="register-input"
          />
          <input
            type="text"
            name="marksInPlusTwo.physics"
            value={formData.marksInPlusTwo.physics}
            onChange={handleChange}
            placeholder="Marks in Physics"
            className="register-input"
          />
          <input
            type="text"
            name="marksInPlusTwo.chemistry"
            value={formData.marksInPlusTwo.chemistry}
            onChange={handleChange}
            placeholder="Marks in Chemistry"
            className="register-input"
          />
          <input
            type="text"
            name="marksInPlusTwo.cutOff"
            value={formData.marksInPlusTwo.cutOff}
            onChange={handleChange}
            placeholder="Cut-off Percentage"
            className="register-input"
          />
          <input
            type="text"
            name="marksInPlusTwo.quota"
            value={formData.marksInPlusTwo.quota}
            onChange={handleChange}
            placeholder="Quota (if applicable)"
            className="register-input"
          />

          {/* Counselor Names */}
          <input
            type="text"
            name="counselorNames.firstYear"
            value={formData.counselorNames.firstYear}
            onChange={handleChange}
            placeholder="Counselor Name (1st Year)"
            className="register-input"
          />
          <input
            type="text"
            name="counselorNames.secondYear"
            value={formData.counselorNames.secondYear}
            onChange={handleChange}
            placeholder="Counselor Name (2nd Year)"
            className="register-input"
          />
          <input
            type="text"
            name="counselorNames.thirdYear"
            value={formData.counselorNames.thirdYear}
            onChange={handleChange}
            placeholder="Counselor Name (3rd Year)"
            className="register-input"
          />
          <input
            type="text"
            name="counselorNames.finalYear"
            value={formData.counselorNames.finalYear}
            onChange={handleChange}
            placeholder="Counselor Name (Final Year)"
            className="register-input"
          />

          {/* Current Semester */}
          <input
            type="text"
            name="currentSemester"
            value={formData.currentSemester}
            onChange={handleChange}
            placeholder="Current Semester"
            className="register-input"
          />

          {/* Subjects per Semester */}
          {renderSubjectTables()}

          <button type="submit" className="register-button">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default StudentRegister;
