import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const FacultyDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { facultyDetails } = location.state || {};

  useEffect(() => {
    if (!facultyDetails) {
      navigate('/faculty-login'); // Redirect to login if state is missing
    }
  }, [facultyDetails, navigate]);

  if (!facultyDetails) {
    return null; // Return null while redirecting
  }

  return (
    <div className="faculty-dashboard">
      <h2>Welcome, {facultyDetails.name}</h2>
      <div>
        <h3>Faculty Details:</h3>
        <p><strong>Email:</strong> {facultyDetails.email}</p>
        <p><strong>Department:</strong> {facultyDetails.department}</p>
        <p><strong>Phone:</strong> {facultyDetails.phone}</p>
        {/* Add more fields as required */}
      </div>
    </div>
  );
};

export default FacultyDashboard;
