import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // To handle redirection

const FacultyLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory(); // Use useHistory for redirection

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous error

    try {
      // Make the API call to login
      const response = await axios.post('http://localhost:5000/api/faculty/login', {
        email,
        password,
      });

      // Save the token (optional) if using JWT or use context/state to store user data
      const { token, facultyDetails } = response.data;

      // Redirect to the new faculty page with faculty details
      history.push({
        pathname: '/faculty-dashboard', // Redirect to the faculty dashboard page
        state: { facultyDetails }, // Pass faculty details as state
      });
    } catch (err) {
      console.error('Login error:', err);
      setError('Invalid credentials, please try again');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      {error && <div className="error-message">{error}</div>}
      <button type="submit">Login</button>
    </form>
  );
};

export default FacultyLogin;
