import React, { useState } from 'react';
import '../styles/Register.css';
import axios from 'axios';

const FacultyRegister = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    branch: '',
    handlingYears: [],
    subjectNames: [{ name: '', code: '' }]
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubjectChange = (index, e) => {
    const newSubjectNames = [...formData.subjectNames];
    newSubjectNames[index][e.target.name] = e.target.value;
    setFormData({ ...formData, subjectNames: newSubjectNames });
  };

  const handleAddSubject = () => {
    setFormData({
      ...formData,
      subjectNames: [...formData.subjectNames, { name: '', code: '' }]
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/faculty/register', formData);
      console.log('Registration successful:', response.data);
      alert('Faculty registered successfully');
    } catch (err) {
      console.error('Error registering faculty:', err);
      alert('Error occurred while registering');
    }
  };

    return (
        <div className="register-container">
            <div className="register-card">
                <h2>Faculty Registration</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Faculty Name"
                        name="name"
                        value={facultyData.name}
                        onChange={handleChange}
                        className="register-input"
                        required
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        name="email"
                        value={facultyData.email}
                        onChange={handleChange}
                        className="register-input"
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={facultyData.password}
                        onChange={handleChange}
                        className="register-input"
                        required
                    />
                    <input
                        type="text"
                        placeholder="Branch"
                        name="branch"
                        value={facultyData.branch}
                        onChange={handleChange}
                        className="register-input"
                    />
                    <input
                        type="text"
                        placeholder="Handling Years"
                        name="handlingYears"
                        value={facultyData.handlingYears}
                        onChange={handleChange}
                        className="register-input"
                    />
                    <input
                        type="text"
                        placeholder="Subject Codes"
                        name="subjectCodes"
                        value={facultyData.subjectCodes.join(', ')}
                        onChange={handleChange}
                        className="register-input"
                    />
                    <button type="submit" className="register-btn">Register Faculty</button>
                </form>
            </div>
        </div>
    );
}

export default FacultyRegister;
