import React, { useState } from 'react';
import axios from 'axios';
import '../styles/'
import { useNavigate } from 'react-router-dom';

const FacultyLogin = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); // Clear previous error

        try {
            // Make the API call to login
            const response = await axios.post('http://localhost:5000/api/faculty/login', {
                email,
                password,
            });

            // Save the token (optional) if using JWT or context/state to store user data
            const { facultyDetails } = response.data;

            // Redirect to the new faculty page with faculty details
            navigate('/faculty-dashboard', { state: { facultyDetails } });
        } catch (err) {
            console.error('Login error:', err);
            setError('Invalid credentials, please try again');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
            {error && <div className="error-message">{error}</div>}
            <button type="submit">Login</button>
        </form>
    );
};

export default FacultyLogin;
