import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

import '../styles/FacultyDashboard.css'; // Styles for full-page layout

const FacultyDashboard = () => {
  const [selectedTab, setSelectedTab] = useState('details');
  const [facultyDetails, setFacultyDetails] = useState(null);
  const [semesters, setSemesters] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedSemester, setSelectedSemester] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchFacultyDetails = async () => {
      try {
        // Check if state is passed during navigation
        const stateDetails = location.state?.facultyDetails;
        let email;

        if (stateDetails) {
          setFacultyDetails(stateDetails);
          return;
        } else {
          // Fallback to localStorage
          email = localStorage.getItem('facultyEmail');
          if (!email) {
            throw new Error('No email found in localStorage');
          }
        }

        // Fetch faculty details from the server
        const response = await axios.get(`http://localhost:5000/api/faculty/details?email=${email}`);
        
        setFacultyDetails(response.data);
      } catch (err) {
        console.error('Error fetching faculty details:', err);
        navigate('/faculty-login'); // Redirect to login page on error
      }
    };

    fetchFacultyDetails();
  }, [location, navigate]);

  if (!facultyDetails) {
    return <p>Loading faculty details...</p>;
  }

  const handleSemesterSelect = async (semester) => {
    setSelectedSemester(semester);

    // Fetch subjects for the selected semester
    try {
      const response = await axios.get(`http://localhost:5000/api/subjects?semester=${semester}`);
      setSubjects(response.data);
    } catch (err) {
      console.error('Error fetching subjects:', err);
    }
  };

  const handleSubjectSelect = async (subjectCode) => {
    setSelectedSubject(subjectCode);

    // Fetch students for the selected subject
    try {
      const response = await axios.get(`http://localhost:5000/api/students?subjectCode=${subjectCode}`);
      setStudents(response.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  const renderTabContent = () => {
    switch (selectedTab) {
      case 'details':
        return facultyDetails ? (
          <div className="details-tab">
            <h2>Faculty Details</h2>
            <p><strong>Name:</strong> {facultyDetails.name}</p>
            <p><strong>Email:</strong> {facultyDetails.email}</p>
            <p><strong>Branch:</strong> {facultyDetails.branch}</p>
            
  
            <div>
              <h3>Handling Years:</h3>
              {facultyDetails.handlingYears && facultyDetails.handlingYears.length > 0 ? (
                <ul>
                  {facultyDetails.handlingYears.map((year, index) => (
                    <li key={index}>{year}</li>
                  ))}
                </ul>
              ) : (
                <p>No handling years available.</p>
              )}
            </div>
  
            <div>
              <h3>Subjects:</h3>
              {facultyDetails.subjectNames && facultyDetails.subjectNames.length > 0 ? (
                <ul>
                  {facultyDetails.subjectNames.map((subject, index) => (
                    <li key={index}>
                      <strong>Name:</strong> {subject.name} <br />
                      <strong>Code:</strong> {subject.code}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No subjects available.</p>
              )}
            </div>
          </div>
        ) : (
          <p>Loading details...</p>
        );

      case 'assessment':
        return (
          <div className="assessment-tab">
            <h2>Assessment</h2>
            <div className="semester-select">
              <label>Select Semester:</label>
              <select onChange={(e) => handleSemesterSelect(e.target.value)} value={selectedSemester}>
                <option value="">-- Select Semester --</option>
                {semesters.map((sem) => (
                  <option key={sem} value={sem}>{sem}</option>
                ))}
              </select>
            </div>

            {selectedSemester && (
              <div className="subject-select">
                <label>Select Subject:</label>
                <select onChange={(e) => handleSubjectSelect(e.target.value)} value={selectedSubject}>
                  <option value="">-- Select Subject --</option>
                  {subjects.map((sub) => (
                    <option key={sub.code} value={sub.code}>{sub.code}</option>
                  ))}
                </select>
              </div>
            )}

            {selectedSubject && (
              <div className="student-list">
                <h3>Students for {selectedSubject}</h3>
                <table>
                  <thead>
                    <tr>
                      <th>First Name</th>
                      <th>CAT 1</th>
                      <th>CAT 2</th>
                      <th>Model</th>
                      <th>Sem Grade</th>
                    </tr>
                  </thead>
                  <tbody>
                    {students.map((student) => (
                      <tr key={student._id}>
                        <td>{student.firstName}</td>
                        <td>{student.cat1 || '-'}</td>
                        <td>{student.cat2 || '-'}</td>
                        <td>{student.model || '-'}</td>
                        <td>{student.semGrade || '-'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        );

      case 'change-course':
        return <p>Change Course functionality coming soon!</p>;

      default:
        return <p>Select a tab to view content</p>;
    }
  };

  return (
    <div className="faculty-dashboard">
      <div className="sidebar">
        <button onClick={() => setSelectedTab('details')}>Add Details</button>
        <button onClick={() => setSelectedTab('assessment')}>Assessment</button>
        <button onClick={() => setSelectedTab('change-course')}>Change Course</button>
      </div>
      <div className="main-content">{renderTabContent()}</div>
    </div>
  );
};

export default FacultyDashboard;
