import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Use useNavigate for navigation

const FacultyLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/api/faculty/login', {
        email,
        password,
      });

      const { facultyDetails } = response.data;

      // Save email to localStorage
      localStorage.setItem('facultyEmail', facultyDetails.email);

      // Redirect to the dashboard
      navigate('/faculty-dashboard', { state: { facultyDetails } });
    } catch (err) {
      console.error('Login error:', err);
      setError('Invalid credentials, please try again');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      {error && <div className="error-message">{error}</div>}
      <button type="submit">Login</button>
    </form>
  );
};

export default FacultyLogin;
