import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/StudentLogin.css";

const StudentLogin = () => {
  const [rollNo, setRollNo] = useState("");
  const [dob, setDob] = useState("");
  const [error] = useState(""); // For error messages
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const payload = { rollNumber: rollNo, dob }; // Match `rollNumber` and `dob` in the database
      const response = await axios.post("http://localhost:5000/api/students/login", payload);
  
      const { token } = response.data; // Assuming the backend returns a token upon successful login
      if (token) {
        localStorage.setItem("studentToken", token);
        localStorage.setItem("rollNumber", rollNo); // Save roll number for future requests
        alert("Login successful!");
        navigate("/dashboard"); // Navigate to the dashboard
      } else {
        alert("Login failed! No token received.");
      }
    } catch (error) {
      console.error("Error during login:", error.response?.data || error.message);
      alert("Login failed. Please check your credentials.");
    }
  };
  
  

  return (
    <form onSubmit={handleSubmit} className="student-login-form">
      <h1><strong>Student Login</strong></h1>
      {error && <p className="error-message">{error}</p>}
      <input
        type="text"
        placeholder="Roll Number"
        value={rollNo}
        onChange={(e) => setRollNo(e.target.value)}
        required
      />
      <input
        type="date"
        placeholder="Date of Birth"
        value={dob}
        onChange={(e) => setDob(e.target.value)}
        required
      />
      <button type="submit">Login</button>
    </form>
  );
};

export default StudentLogin;
