import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/StudentDashboard.css";

const StudentDashboard = () => {
  const [selectedTab, setSelectedTab] = useState("details");
  const [studentDetails, setStudentDetails] = useState(null);
  const [results, setResults] = useState({});
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
  
        const token = localStorage.getItem("studentToken");
        const rollNumber = localStorage.getItem("rollNumber");
  
        if (!token || !rollNumber) {
          console.error("Token or roll number missing, redirecting to login.");
          navigate("/student-login");
          return;
        }
  
        const headers = { Authorization: `Bearer ${token}` };
  
        // Fetch student details
        const detailsResponse = await axios.get(
          `http://localhost:5000/api/student/details?rollNumber=${rollNumber}`,
          { headers }
        );
        setStudentDetails(detailsResponse.data);
  
        // Fetch student results
        const resultsResponse = await axios.get(
          `http://localhost:5000/api/student/results?rollNumber=${rollNumber}`,
          { headers }
        );
        setResults(resultsResponse.data);
      } catch (error) {
        console.error("Error during data fetch:", error.message);
        alert("Failed to fetch data. Please login again.");
        handleLogout(); // Clear localStorage and redirect to login
      } finally {
        setLoading(false);
      }
    };
  
    fetchData();
  }, [navigate]);
  

  const handleLogout = () => {
    localStorage.removeItem("studentToken");
    localStorage.removeItem("rollNumber");
    navigate("/student-login");
  };
  

  const renderTabContent = () => {
    if (loading) {
      return <p>Loading data...</p>;
    }

    switch (selectedTab) {
      case "details":
        return (
          <div className="details-tab">
            <h2>Student Details</h2>
            <button className="logout" onClick={handleLogout}>
              Logout
            </button>
            {studentDetails ? (
              <>
                <p>
                  <strong>Name:</strong> {studentDetails.name}
                </p>
                <p>
                  <strong>Email:</strong> {studentDetails.email}
                </p>
                <p>
                  <strong>Roll Number:</strong> {studentDetails.rollNumber}
                </p>
                <p>
                  <strong>Branch:</strong> {studentDetails.branch || "N/A"}
                </p>
                <p>
                  <strong>Semester:</strong> {studentDetails.semester}
                </p>
              </>
            ) : (
              <p>No details available.</p>
            )}
          </div>
        );

      case "attendance":
        return (
          <div className="attendance-tab">
            <h2>Attendance Percentage</h2>
            <p>Coming soon!</p>
          </div>
        );

      case "results":
        return (
          <div className="results-tab">
            <h2>Results</h2>
            {Object.keys(results).length > 0 ? (
              <div className="results-container">
                {Object.keys(results).map((semester) => (
                  <div key={semester} className="semester-table">
                    <h3>Semester {semester}</h3>
                    <table>
                      <thead>
                        <tr>
                          <th>Subject Code</th>
                          <th>CAT 1</th>
                          <th>CAT 2</th>
                          <th>Model</th>
                          <th>Sem Grade</th>
                        </tr>
                      </thead>
                      <tbody>
                        {results[semester].map((subject, index) => (
                          <tr key={index}>
                            <td>{subject.subjectCode}</td>
                            <td>{subject.cat1 || "-"}</td>
                            <td>{subject.cat2 || "-"}</td>
                            <td>{subject.model || "-"}</td>
                            <td>{subject.semGrade || "-"}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ))}
              </div>
            ) : (
              <p>No results available.</p>
            )}
          </div>
        );

      default:
        return <p>Select a tab to view content</p>;
    }
  };

  return (
    <div className="student-dashboard">
      <div className="sidebar">
        <button onClick={() => setSelectedTab("details")}>Details</button>
        <button onClick={() => setSelectedTab("attendance")}>
          Attendance Percentage
        </button>
        <button onClick={() => setSelectedTab("results")}>Results</button>
      </div>
      <div className="main-content">{renderTabContent()}</div>
    </div>
  );
};

export default StudentDashboard;
